import { QuizData } from "./QuizData";

export default function Quiz() {
  return (
    <>
      <div>
        <p className="heading-txt">Quiz App</p>
        <div className="container">
          <div className="question">
            <span id="question-number">1.</span>
            <span id="question-txt">{QuizData[0].question}</span>
          </div>
          <div className="option-container">
            {/* Add four option buttons */}
            {QuizData[0].options.map((option, i) => {
              return (
                <button className="option-btn" key={i}>
                  {option}
                </button>
              );
            })}
            {/* Add three more option buttons */}
            <button className="option-btn" key={i}>
            {option}
            </button>
            <button className="option-btn" key={i}>
              Option 3
            </button>
            <button className="option-btn" key={i}>
              Option 4
            </button>
          </div>
          <input type="button" value="Next" id="next-button" />
        </div>
      </div>
    </>
  );
}
