import React, { useState, useEffect } from 'react';
import './App.css';
import Quiz from './Quiz';
import bImage from './img/b.jpeg';
import mnImage from './img/mn.jpeg';
import mImage from './img/m.jpeg';
import uImage from './img/u.jpeg';
import hImage from './img/h.jpeg';
import cImage from './img/c.jpeg';

function App() {
  const images = [bImage, uImage, hImage, mImage, cImage, mImage, cImage, mnImage, hImage, uImage, bImage];
  const [flippedIndexes, setFlippedIndexes] = useState([]);
  const [matchedIndexes, setMatchedIndexes] = useState([]);
  const [showImages, setShowImages] = useState(false); // State to track button click

  const flipImage = (index) => {
    if (!showImages && flippedIndexes.length < 2 && !flippedIndexes.includes(index) && !matchedIndexes.includes(index)) {
      setFlippedIndexes([...flippedIndexes, index]);
    }
  };

  const handleShowImages = () => {
    setShowImages(true);
  };

  useEffect(() => {
    if (flippedIndexes.length === 2 && !showImages) {
      const [index1, index2] = flippedIndexes;
      if (images[index1] === images[index2]) {
        setMatchedIndexes((prevMatches) => [...prevMatches, index1, index2]);
      }
      setTimeout(() => {
        setFlippedIndexes([]);
      }, 1000);
    }
  }, [flippedIndexes, images, showImages]);

  return (
    <div className="App">
      <div className="image-container">
        {images.map((image, index) => (
          <div
            key={index}
            className={`image-card ${flippedIndexes.includes(index) || matchedIndexes.includes(index) ? 'flipped' : ''}`}
            onClick={() => flipImage(index)}
          >
            {showImages || matchedIndexes.includes(index) ? <img src={image} alt={`Image ${index}`} /> : null}
          </div>
        ))}
        {!showImages && matchedIndexes.length === images.length ? (
          <button onClick={handleShowImages}>Show Images</button>
        ) : null}
        <Quiz />
      </div>
    </div>
  );
}

export default App;
