import React, { useState, useEffect } from 'react';
import Photo from './Photo'; // Make sure you have the correct import path for the Photo component
import bImage from './img/b.jpeg';
import mnImage from './img/mn.jpeg';
import mImage from './img/m.jpeg';
import uImage from './img/u.jpeg';
import hImage from './img/h.jpeg';
import cImage from './img/c.jpeg';

export default function Cards() {
  const [cards, setCards] = useState([
    { id: 0, name: 'b', status: 'hidden', img: bImage },
    { id: 1, name: 'mn', status: 'hidden', img: mnImage },
    { id: 2, name: 'm', status: 'hidden', img: mImage },
    { id: 3, name: 'u', status: 'hidden', img: uImage },
    { id: 4, name: 'h', status: 'hidden', img: hImage },
    { id: 5, name: 'c', status: 'hidden', img: cImage }, 
  ].map((card) => ({ ...card, id: Math.random() }))); // Ensure each card has a unique ID

  const [flippedCards, setFlippedCards] = useState([]);

  useEffect(() => {
    if (flippedCards.length === 2) {
      checkForMatch();
    }
  }, [flippedCards]);

  const flipCard = (id) => {
    if (flippedCards.length < 2 && cards[id].status !== 'matched') {
      setCards((prevCards) =>
        prevCards.map((card) =>
          card.id === id ? { ...card, status: 'visible' } : card
        )
      );
      setFlippedCards((prevFlipped) => [...prevFlipped, id]);
    }
  };

  const checkForMatch = () => {
    const [cardId1, cardId2] = flippedCards; 
    if (cards[cardId1].name === cards[cardId2].name) {
      setCards((prevCards) =>
        prevCards.map((card, index) =>
          index === cardId1 || index === cardId2 ? { ...card, status: 'matched' } : card
        )
      );
    } else {
      setTimeout(() => {
        setCards((prevCards) =>
          prevCards.map((card, index) =>
            index === cardId1 || index === cardId2 ? { ...card, status: 'hidden' } : card
          )
        );
      }, 1000);
    }
    setFlippedCards([]);
  };

  return (
    <div className="main" style={{ display: 'flex', flexWrap: 'wrap' }}>
      {cards.map((card, index) => (
        <Photo
          key={card.id} // Use unique card ID as the key
          card={card}
          onClick={() => flipCard(index)}
        />
      ))}
    </div>
  );
}
