import React, { useState } from 'react';
import './App.css'; // You can adjust the CSS as needed
import Cards from './Cards';
// Import your images here
import bImage from './img/b.jpeg';
import mnImage from './img/mn.jpeg';
import mImage from './img/m.jpeg';
import uImage from './img/u.jpeg';
import hImage from './img/h.jpeg';
import cImage from './img/c.jpeg';

function App() {
  const images = [bImage, mnImage, mImage, cImage, hImage, uImage]; // Add your image imports to this array
  const [flippedIndexes, setFlippedIndexes] = useState([]);

  const flipImage = (index) => {
    if (!flippedIndexes.includes(index)) {
      setFlippedIndexes([...flippedIndexes, index]);
    }
  };

  return (
    <div className="App">
      <div className="image-container">
        {images.map((image, index) => (
          <div
            key={index}
            className={`image-card ${flippedIndexes.includes(index) ? 'flipped' : ''}`}
            onClick={() => flipImage(index)}
          >
            <img src={image} alt={`Image ${index}`} />
            <Cards />
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;












